import React, { useEffect, useState } from "react";
import API from "../api";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [companyId, setCompanyId] = useState("");
  const [companies, setCompanies] = useState([]);

  const fetchJobs = async () => {
    const res = await API.get("/jobs");
    setJobs(res.data);
  };

  const fetchCompanies = async () => {
    const res = await API.get("/companies");
    setCompanies(res.data);
  };

  useEffect(() => {
    fetchJobs();
    fetchCompanies();
  }, []);

  const handleAdd = async (e) => {
    e.preventDefault();
    await API.post("/jobs", { title, description, companyId });
    setTitle("");
    setDescription("");
    setCompanyId("");
    fetchJobs();
  };

  const handleDelete = async (id) => {
    await API.delete(`/jobs/${id}`);
    fetchJobs();
  };

  return (
    <div>
      <h2 style={{ color: "#6b3fa0" }}>Job Management</h2>

      <form onSubmit={handleAdd} style={{ marginBottom: "20px" }}>
        <input placeholder="Title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <input placeholder="Description" value={description} onChange={(e) => setDescription(e.target.value)} required />
        <select value={companyId} onChange={(e) => setCompanyId(e.target.value)} required>
          <option value="">Select Company</option>
          {companies.map((c) => (
            <option key={c._id} value={c._id}>{c.name}</option>
          ))}
        </select>
        <button type="submit" style={{ background: "#6b3fa0", color: "white" }}>Add</button>
      </form>

      <table style={{ width: "100%", borderCollapse: "collapse" }}>
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={{ padding: "10px" }}>Title</th>
            <th>Description</th>
            <th>Company</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {jobs.map((j) => (
            <tr key={j._id} style={{ borderBottom: "1px solid #ddd" }}>
              <td style={{ padding: "10px" }}>{j.title}</td>
              <td>{j.description}</td>
              <td>{j.company?.name}</td>
              <td><button onClick={() => handleDelete(j._id)}>Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
