import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import ProfileDialog from "./ProfileDialog";

export default function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const [showProfile, setShowProfile] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <nav style={styles.navbar}>
        <div style={styles.container}>
          {/* Logo */}
          <Link to="/" style={styles.logo}>
            <img src="/logo.png" alt="Logo" style={styles.logoImg} />
            <span style={styles.logoText}>JobFinder</span>
          </Link>

          {/* Links */}
          <ul style={styles.ul}>
            <li>
              <Link to="/jobs" style={styles.link}>
                Jobs
              </Link>
            </li>

            {token ? (
              <>
                <li>
                  <Link to="/admin" style={styles.link}>
                    Dashboard
                  </Link>
                </li>
                <li style={styles.dropdownWrapper}>
                  <button style={styles.profileBtn}>
                    {user?.name || "Profile"} â–¼
                  </button>
                  <div style={styles.dropdown}>
                    <button
                      style={styles.dropdownItem}
                      onClick={() => setShowProfile(true)}
                    >
                      My Profile
                    </button>
                    <button
                      onClick={handleLogout}
                      style={{
                        ...styles.dropdownItem,
                        borderTop: "1px solid #ddd",
                      }}
                    >
                      Logout
                    </button>
                  </div>
                </li>
              </>
            ) : (
              <>
                <li>
                  <Link to="/login" style={styles.link}>
                    Login
                  </Link>
                </li>
                <li>
                  <Link to="/register" style={styles.registerBtn}>
                    Register
                  </Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </nav>

      {showProfile && <ProfileDialog onClose={() => setShowProfile(false)} />}
    </>
  );
}

const styles = {
  navbar: {
    width: "100%",
    background:
      "linear-gradient(135deg, #e0c3fc 0%, #f0b4f1ff 100%)",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
    position: "sticky",
    top: 0,
    zIndex: 1000,
  },
  container: {
    maxWidth: "1200px",
    margin: "0 auto",
    padding: "12px 24px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  logo: {
    display: "flex",
    alignItems: "center",
    textDecoration: "none",
    color: "#333",
    fontWeight: "bold",
    fontSize: "20px",
