import React, { useEffect, useState } from "react";
import API from "../api";

export default function UserList() {
  const [users, setUsers] = useState([]);

  // Get backend URL for images
  const BACKEND_URL = import.meta.env.VITE_BACKEND_URL || "http://localhost:7000";

  useEffect(() => {
    API.get("/auth/users")
      .then((res) => {
        console.log("Fetched users:", res.data);
        setUsers(res.data);
      })
      .catch((err) => console.error("Error fetching users:", err));
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ color: "#6b3fa0" }}>User List</h2>
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          marginTop: "15px",
          textAlign: "left",
        }}
      >
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={{ padding: "10px" }}>Image</th>
            <th style={{ padding: "10px" }}>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u) => (
            <tr key={u._id} style={{ borderBottom: "1px solid #ddd" }}>
              <td style={{ padding: "10px" }}>
                <img
                  src={
                    u.image?.startsWith("http")
                      ? u.image
                      : `${BACKEND_URL}${u.image}`
                  }
                  alt={u.name}
                  style={{
                    width: "60px",
                    height: "60px",
                    borderRadius: "50%",
                    objectFit: "cover",
                    border: "1px solid #ccc",
                  }}
                  onError={(e) => {
                    e.target.src = "/default-avatar.png";
                  }}
                />
              </td>
              <td style={{ padding: "10px" }}>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.phone || "—"}</td>
              <td>{u.role}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
