import React, { useState } from 'react';
import API from '../api';

export default function Register() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [phone, setPhone] = useState('');
  const [skills, setSkills] = useState([]);
  const [image, setImage] = useState(null);
  const [cv, setCv] = useState(null);

  const availableSkills = [
    'HTML',
    'CSS',
    'JavaScript',
    'React',
    'Node.js',
    'Express',
    'MongoDB',
    'Python',
    'Java',
    'C++',
  ];

  const handle = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('email', email);
      formData.append('password', password);
      formData.append('phone', phone);
      formData.append('skills', skills.join(',')); // join selected skills
      if (image) formData.append('image', image);
      if (cv) formData.append('cv', cv);

      const res = await API.post('/auth/register', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      window.location.href = '/jobs';
    } catch (err) {
      alert(err.response?.data?.message || 'Register failed');
    }
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.navbar}>Job Tracker</div>

      <div style={styles.cardContainer}>
        <form onSubmit={handle} style={styles.card} encType="multipart/form-data">
          <h2 style={styles.title}>Create an Account</h2>

          <div style={styles.row}>
            <div style={styles.field}>
              <label style={styles.label}>Full Name</label>
              <input
                style={styles.input}
                value={name}
                onChange={e => setName(e.target.value)}
                required
              />
            </div>

            <div style={styles.field}>
              <label style={styles.label}>Email</label>
              <input
                type="email"
                style={styles.input}
                value={email}
                onChange={e => setEmail(e.target.value)}
                required
              />
            </div>
          </div>

          <div style={styles.row}>
            <div style={styles.field}>
              <label style={styles.label}>Password</label>
              <input
                type="password"
                style={styles.input}
                value={password}
                onChange={e => setPassword(e.target.value)}
                required
              />
            </div>

            <div style={styles.field}>
              <label style={styles.label}>Phone</label>
              <input
                style={styles.input}
                value={phone}
                onChange={e => setPhone(e.target.value)}
              />
            </div>
          </div>

          <div style={styles.row}>
            <div style={styles.field}>
              <label style={styles.label}>Skills</label>
              <select
                multiple
                style={styles.select}
                value={skills}
                onChange={(e) =>
                  setSkills(Array.from(e.target.selectedOptions, (option) => option.value))
                }
              >
                {availableSkills.map((skill) => (
                  <option key={skill} value={skill}>
                    {skill}
                  </option>
                ))}
              </select>
              <small style={{ color: '#555' }}>Hold Ctrl (Windows) or Cmd (Mac) to select multiple</small>
            </div>

            <div style={styles.field}>
              <label style={styles.label}>Profile Image</label>
              <input
                type="file"
                style={styles.inputFile}
                accept="image/*"
                onChange={e => setImage(e.target.files[0])}
              />
            </div>
          </div>

          <div style={styles.row}>
            <div style={{ ...styles.field, width: '100%' }}>
              <label style={styles.label}>CV (optional)</label>
              <input
                type="file"
                style={styles.inputFile}
                accept=".pdf,.doc,.docx"
                onChange={e => setCv(e.target.files[0])}
              />
            </div>
          </div>

          <button style={styles.button}>Register</button>

          <p style={styles.linkText}>
            Already have an account?{' '}
            <a href="/login" style={styles.link}>
              Login here
            </a>
          </p>
        </form>
      </div>
    </div>
  );
}

