import React, { useState, useEffect } from "react";
import API from "../api";

export default function ProfileDialog({ onClose }) {
  const [user, setUser] = useState({ name: "", email: "", phone: "", skills: "" });
  const [editing, setEditing] = useState(false);

  useEffect(() => {
    API.get("/profile")
      .then((res) => setUser({
        ...res.data,
        skills: res.data.skills ? res.data.skills.join(", ") : "",
      }))
      .catch((err) => console.error(err));
  }, []);

  const handleChange = (e) => setUser({ ...user, [e.target.name]: e.target.value });

  const handleUpdate = async () => {
    try {
      const res = await API.patch("/profile", user);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      setEditing(false);
      alert("Profile updated successfully!");
    } catch (err) {
      alert("Failed to update profile");
    }
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-[1000]">
      <div className="bg-white rounded-xl shadow-lg w-96 p-6 relative">
        <button onClick={onClose} className="absolute right-3 top-2 text-gray-500 hover:text-gray-800">
          âœ–
        </button>

        <h2 className="text-xl font-semibold text-center text-purple-700 mb-4">
          My Profile
        </h2>

        <div className="space-y-3">
          <input
            type="text"
            name="name"
            value={user.name}
            onChange={handleChange}
            disabled={!editing}
            placeholder="Name"
            className={`w-full px-3 py-2 border rounded-lg ${editing ? "border-purple-400" : "bg-gray-100"}`}
          />
          <input
            type="email"
            name="email"
            value={user.email}
            onChange={handleChange}
            disabled
            placeholder="Email"
            className="w-full px-3 py-2 border rounded-lg bg-gray-100"
          />
          <input
            type="text"
            name="phone"
            value={user.phone || ""}
            onChange={handleChange}
            disabled={!editing}
            placeholder="Phone"
            className={`w-full px-3 py-2 border rounded-lg ${editing ? "border-purple-400" : "bg-gray-100"}`}
          />
          <input
            type="text"
            name="skills"
            value={user.skills || ""}
            onChange={handleChange}
            disabled={!editing}
            placeholder="Skills (comma separated)"
            className={`w-full px-3 py-2 border rounded-lg ${editing ? "border-purple-400" : "bg-gray-100"}`}
          />
        </div>

        <div className="flex justify-between mt-5">
          {!editing ? (
            <button
              onClick={() => setEditing(true)}
              className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
            >
              Edit
            </button>
          ) : (
            <button
              onClick={handleUpdate}
              className="bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
            >
              Update
            </button>
          )}
          <button
            onClick={onClose}
            className="bg-gray-300 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-400"
          >
            Close
          </button>
        </div>
      </div>
    </div>
  );
}
