import React, { useEffect, useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";
import API from "../api";

export default function DashboardHome() {
  const [stats, setStats] = useState({
    users: 0,
    jobs: 0,
    companies: 0,
  });
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      // Get token from localStorage
      const token = localStorage.getItem("token");
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };

      // Fetch all three in parallel
      const [usersRes, jobsRes, companiesRes] = await Promise.all([
        API.get("/users", config),
        API.get("/jobs", config),
        API.get("/companies", config),
      ]);

      const users = Array.isArray(usersRes.data) ? usersRes.data.length : usersRes.data.count || 0;
      const jobs = Array.isArray(jobsRes.data) ? jobsRes.data.length : jobsRes.data.count || 0;
      const companies = Array.isArray(companiesRes.data)
        ? companiesRes.data.length
        : companiesRes.data.count || 0;

      // Update stats
      setStats({ users, jobs, companies });

      // Prepare chart data
      setChartData([
        { name: "Users", value: users },
        { name: "Jobs", value: jobs },
        { name: "Companies", value: companies },
      ]);

      setLoading(false);
    } catch (err) {
      console.error("Dashboard fetch error:", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: "20px", textAlign: "center", color: "#6b3fa0" }}>
        <h3>Loading Dashboard Data...</h3>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ color: "#6b3fa0", marginBottom: "20px" }}>Dashboard Overview</h2>

      {/* Stat Cards */}
      <div
        style={{
          display: "flex",
          gap: "20px",
          marginBottom: "40px",
          flexWrap: "wrap",
        }}
      >
        {/* Users Card */}
        <div
          style={{
            flex: "1",
            background: "#ede4ff",
            borderRadius: "10px",
            padding: "20px",
            minWidth: "220px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          }}
        >
          <h3 style={{ color: "#6b3fa0" }}>Total Users</h3>
          <p style={{ fontSize: "28px", fontWeight: "bold" }}>{stats.users}</p>
        </div>

        {/* Jobs Card */}
        <div
          style={{
            flex: "1",
            background: "#e8f6ff",
            borderRadius: "10px",
            padding: "20px",
            minWidth: "220px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          }}
        >
          <h3 style={{ color: "#007bff" }}>Total Jobs</h3>
          <p style={{ fontSize: "28px", fontWeight: "bold" }}>{stats.jobs}</p>
        </div>

        {/* Companies Card */}
        <div
          style={{
            flex: "1",
            background: "#fdf2e9",
            borderRadius: "10px",
            padding: "20px",
            minWidth: "220px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          }}
        >
          <h3 style={{ color: "#ff8c00" }}>Total Companies</h3>
          <p style={{ fontSize: "28px", fontWeight: "bold" }}>{stats.companies}</p>
        </div>
      </div>

      {/* Chart */}
      <div
        style={{
          background: "#fff",
          padding: "20px",
          borderRadius: "10px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          height: "350px",
        }}
      >
        <h3 style={{ color: "#6b3fa0", marginBottom: "10px" }}>
          Graphical Representation
        </h3>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis allowDecimals={false} />
            <Tooltip />
            <Bar dataKey="value" fill="#6b3fa0" radius={[5, 5, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
