import React, { useEffect, useState } from "react";
import API from "../api";

export default function SkillManager() {
  const [skills, setSkills] = useState([]);
  const [newSkill, setNewSkill] = useState("");
  const [loading, setLoading] = useState(true);
  const [userRole, setUserRole] = useState("");

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    setUserRole(user?.role);
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/skills/list", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSkills(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  const handleAddSkill = async () => {
    if (!newSkill.trim()) return alert("Enter a skill name!");
    try {
      const token = localStorage.getItem("token");
      await API.post(
        "/skills/add",
        { name: newSkill },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Skill added successfully!");
      setNewSkill("");
      fetchSkills();
    } catch (err) {
      alert(err.response?.data?.message || "Failed to add skill");
    }
  };

  if (loading) return <p style={{ textAlign: "center" }}>Loading...</p>;

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Skill Management</h2>

      {userRole === "admin" && (
        <div style={styles.addContainer}>
          <input
            type="text"
            value={newSkill}
            onChange={(e) => setNewSkill(e.target.value)}
            placeholder="Enter new skill"
            style={styles.input}
          />
          <button onClick={handleAddSkill} style={styles.addBtn}>
            Add Skill
          </button>
        </div>
      )}

      <div style={styles.listContainer}>
        <h3 style={{ marginBottom: "10px" }}>Skill List</h3>
        {skills.length === 0 ? (
          <p>No skills added yet.</p>
        ) : (
          <ul style={styles.list}>
            {skills.map((s) => (
              <li key={s._id} style={styles.listItem}>
                {s.name}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "600px",
    margin: "30px auto",
    padding: "20px",
    background: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  },
  heading: {
    textAlign: "center",
    color: "#6b3fa0",
    marginBottom: "20px",
  },
  addContainer: {
    display: "flex",
    gap: "10px",
    marginBottom: "20px",
  },
  input: {
    flex: 1,
    padding: "10px",
    borderRadius: "6px",
    border: "1px solid #ccc",
  },
  addBtn: {
    background: "#6b3fa0",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    padding: "10px 15px",
    cursor: "pointer",
  },
  listContainer: { marginTop: "20px" },
  list: {
    listStyle: "none",
    padding: 0,
    display: "grid",
    gap: "10px",
  },
  listItem: {
    background: "#f9f7fc",
    borderRadius: "8px",
    padding: "10px",
    border: "1px solid #e3d7f3",
    color: "#333",
    fontWeight: "500",
  },
};
