import React, { useEffect, useState } from "react";
import API from "../api";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [companies, setCompanies] = useState([]);

  // form states
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [companyId, setCompanyId] = useState("");
  const [location, setLocation] = useState("");
  const [category, setCategory] = useState("");
  const [type, setType] = useState("");
  const [skills, setSkills] = useState([]);

  // dialog visibility
  const [showDialog, setShowDialog] = useState(false);

  const availableSkills = [
    "HTML",
    "CSS",
    "JavaScript",
    "React",
    "Node.js",
    "Express",
    "MongoDB",
    "Python",
    "Java",
    "C++",
  ];

  const fetchJobs = async () => {
    const res = await API.get("/jobs");
    setJobs(res.data);
  };

  const fetchCompanies = async () => {
    const res = await API.get("/companies");
    setCompanies(res.data);
  };

  useEffect(() => {
    fetchJobs();
    fetchCompanies();
  }, []);

  const handleAdd = async (e) => {
    e.preventDefault();
    try {
      await API.post("/jobs", {
        title,
        description,
        companyId,
        location,
        category,
        type,
        skills,
      });

      // reset
      setTitle("");
      setDescription("");
      setCompanyId("");
      setLocation("");
      setCategory("");
      setType("");
      setSkills([]);

      setShowDialog(false);
      fetchJobs();
    } catch (err) {
      alert(err.response?.data?.message || "Add job failed");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this job?")) {
      await API.delete(`/jobs/${id}`);
      fetchJobs();
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Job Management</h2>

      <div style={styles.topBar}>
        <button style={styles.addBtn} onClick={() => setShowDialog(true)}>
          + Add Job
        </button>
      </div>

      <table style={styles.table}>
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={styles.th}>Title</th>
            <th style={styles.th}>Description</th>
            <th style={styles.th}>Company</th>
            <th style={styles.th}>Location</th>
            <th style={styles.th}>Category</th>
            <th style={styles.th}>Type</th>
            <th style={styles.th}>Skills</th>
            <th style={styles.th}>Action</th>
          </tr>
        </thead>
        <tbody>
          {jobs.map((j) => (
            <tr key={j._id} style={styles.tr}>
              <td style={styles.td}>{j.title}</td>
              <td style={styles.td}>{j.description}</td>
              <td style={styles.td}>{j.company?.name}</td>
              <td style={styles.td}>{j.location}</td>
              <td style={styles.td}>{j.category}</td>
              <td style={styles.td}>{j.type}</td>
              <td style={styles.td}>{j.skills?.join(", ")}</td>
              <td style={styles.td}>
                <button
                  onClick={() => handleDelete(j._id)}
                  style={styles.deleteBtn}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Dialog box */}
      {showDialog && (
        <div style={styles.overlay}>
          <div style={styles.dialog}>
            <h3 style={styles.dialogTitle}>Add New Job</h3>
            <form onSubmit={handleAdd} style={styles.form}>
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Title</label>
                  <input
                    style={styles.input}
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                  />
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Description</label>
                  <input
                    style={styles.input}
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                  />
                </div>
              </div>

              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Company</label>
                  <select
                    style={styles.input}
                    value={companyId}
                    onChange={(e) => setCompanyId(e.target.value)}
                    required
                  >
                    <option value="">Select Company</option>
                    {companies.map((c) => (
                      <option key={c._id} value={c._id}>
                        {c.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div style={styles.field}>
                  <label style={styles.label}>Location</label>
                  <input
                    style={styles.input}
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                  />
                </div>
              </div>

              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Category</label>
                  <input
                    style={styles.input}
                    value={category}
                    onChange={(e) => setCategory(e.target.value)}
                  />
                </div>

                <div style={styles.field}>
                  <label style={styles.label}>Job Type</label>
                  <select
                    style={styles.input}
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                    required
                  >
                    <option value="">Select Type</option>
                    <option value="full-time">Full-Time</option>
                    <option value="part-time">Part-Time</option>
                    <option value="internship">Internship</option>
                  </select>
                </div>
              </div>

              <div style={styles.field}>
                <label style={styles.label}>Required Skills</label>
                <select
                  multiple
                  style={styles.multiselect}
                  value={skills}
                  onChange={(e) =>
                    setSkills(
                      Array.from(e.target.selectedOptions, (opt) => opt.value)
                    )
                  }
                >
                  {availableSkills.map((skill) => (
                    <option key={skill} value={skill}>
                      {skill}
                    </option>
                  ))}
                </select>
                <small style={{ color: "#555" }}>
                  Hold Ctrl (Windows) or Cmd (Mac) to select multiple
                </small>
              </div>

              <div style={styles.btnRow}>
                <button type="submit" style={styles.saveBtn}>
                  Save
                </button>
                <button
                  type="button"
                  style={styles.cancelBtn}
                  onClick={() => setShowDialog(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

