import React, { useEffect, useState } from "react";
import API from "../api";

export default function UserList() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    API.get("/auth/users")
      .then((res) => setUsers(res.data))
      .catch((err) => console.error(err));
  }, []);

  const BASE_URL = import.meta.env.REACT_APP_API_URL || "http://localhost:7000";

  // Helper to get full image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return "/default-avatar.png";

    // If it's already a full URL (like from Google or external)
    if (imagePath.startsWith("http")) return imagePath;

    // Otherwise, it's a server-relative path (e.g. /uploads/...)
    return `${BASE_URL}${imagePath.startsWith("/") ? "" : "/"}${imagePath}`;
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ color: "#6b3fa0" }}>User List</h2>
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          marginTop: "15px",
          textAlign: "left",
        }}
      >
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={{ padding: "10px" }}>Image</th>
            <th style={{ padding: "10px" }}>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Role</th>
          </tr>
        </thead>
        <tbody>
          {users.map((u) => (
            <tr key={u._id} style={{ borderBottom: "1px solid #ddd" }}>
              <td style={{ padding: "10px" }}>
                <img
                  src={getImageUrl(u.image)}
                  alt={u.name}
                  style={{
                    width: "50px",
                    height: "50px",
                    borderRadius: "50%",
                    objectFit: "cover",
                    border: "1px solid #ccc",
                  }}
                  onError={(e) => (e.target.src = "/default-avatar.png")}
                />
              </td>
              <td style={{ padding: "10px" }}>{u.name}</td>
              <td>{u.email}</td>
              <td>{u.phone || "-"}</td>
              <td>{u.role}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
