import React, { useEffect, useState } from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  ResponsiveContainer,
  CartesianGrid,
} from "recharts";
import API from "../api";

export default function DashboardHome() {
  const [stats, setStats] = useState({
    users: 0,
    jobs: 0,
    companies: 0,
  });
  const [chartData, setChartData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      setError(null);

      const token = localStorage.getItem("token");
      // log token
      console.log("Dashboard fetch: token =", token);

      const config = token
        ? { headers: { Authorization: `Bearer ${token}` } }
        : {};

      const [usersRes, jobsRes, companiesRes] = await Promise.all([
        API.get("/users", config),
        API.get("/jobs", config),
        API.get("/companies", config),
      ]);

      console.log("usersRes.data =", usersRes.data);
      console.log("jobsRes.data =", jobsRes.data);
      console.log("companiesRes.data =", companiesRes.data);

      const usersCount = Array.isArray(usersRes.data)
        ? usersRes.data.length
        : typeof usersRes.data.count === "number"
        ? usersRes.data.count
        : 0;

      const jobsCount = Array.isArray(jobsRes.data)
        ? jobsRes.data.length
        : typeof jobsRes.data.count === "number"
        ? jobsRes.data.count
        : 0;

      const companiesCount = Array.isArray(companiesRes.data)
        ? companiesRes.data.length
        : typeof companiesRes.data.count === "number"
        ? companiesRes.data.count
        : 0;

      setStats({
        users: usersCount,
        jobs: jobsCount,
        companies: companiesCount,
      });

      setChartData([
        { name: "Users", value: usersCount },
        { name: "Jobs", value: jobsCount },
        { name: "Companies", value: companiesCount },
      ]);
    } catch (err) {
      console.error("Dashboard fetch error:", err);
      setError("Failed to fetch dashboard data");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div style={{ padding: "20px", textAlign: "center", color: "#6b3fa0" }}>
        <h3>Loading Dashboard Dataâ€¦</h3>
      </div>
    );
  }

  if (error) {
    return (
      <div style={{ padding: "20px", textAlign: "center", color: "red" }}>
        <h3>{error}</h3>
      </div>
    );
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ color: "#6b3fa0", marginBottom: "20px" }}>
        Dashboard Overview
      </h2>

      {/* Stat Cards */}
      <div
        style={{
          display: "flex",
          gap: "20px",
          marginBottom: "40px",
          flexWrap: "wrap",
        }}
      >
        <div
          style={{
            flex: "1",
            background: "#ede4ff",
            borderRadius: "10px",
            padding: "20px",
            minWidth: "220px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          }}
        >
          <h3 style={{ color: "#6b3fa0" }}>Total Users</h3>
          <p style={{ fontSize: "28px", fontWeight: "bold" }}>{stats.users}</p>
        </div>

        <div
          style={{
            flex: "1",
            background: "#e8f6ff",
            borderRadius: "10px",
            padding: "20px",
            minWidth: "220px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          }}
        >
          <h3 style={{ color: "#007bff" }}>Total Jobs</h3>
          <p style={{ fontSize: "28px", fontWeight: "bold" }}>{stats.jobs}</p>
        </div>

        <div
          style={{
            flex: "1",
            background: "#fdf2e9",
            borderRadius: "10px",
            padding: "20px",
            minWidth: "220px",
            boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          }}
        >
          <h3 style={{ color: "#ff8c00" }}>Total Companies</h3>
          <p style={{ fontSize: "28px", fontWeight: "bold" }}>
            {stats.companies}
          </p>
        </div>
      </div>

      {/* Chart */}
      <div
        style={{
          background: "#fff",
          padding: "20px",
          borderRadius: "10px",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          height: "350px",
        }}
      >
        <h3 style={{ color: "#6b3fa0", marginBottom: "10px" }}>
          Graphical Representation
        </h3>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={chartData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis allowDecimals={false} />
            <Tooltip />
            <Bar dataKey="value" fill="#6b3fa0" radius={[5, 5, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
