import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const [showProfile, setShowProfile] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <>
      <nav className="w-full bg-gradient-to-r from-purple-300 via-pink-200 to-white shadow-md sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-3 flex items-center justify-between">
          {/* Logo */}
          <Link to="/" className="flex items-center gap-2">
            <img src="/logo.png" alt="Logo" className="w-10 h-10 rounded-lg" />
            <span className="text-xl font-bold text-gray-800">JobFinder</span>
          </Link>

          {/* Links */}
          <ul className="flex items-center gap-6">
            <li>
              <Link to="/jobs" className="text-gray-700 hover:text-purple-700 font-medium">
                Jobs
              </Link>
            </li>

            {token ? (
              <>
                <li>
                  <Link to="/admin" className="text-gray-700 hover:text-purple-700 font-medium">
                    Dashboard
                  </Link>
                </li>
                <li className="relative group">
                  <button className="text-gray-700 font-medium">
                    {user?.name || "Profile"} ‚¨áÔ∏è
                  </button>

                  {/* Dropdown */}
                  <div className="absolute right-0 hidden group-hover:block bg-white border rounded-lg shadow-md mt-2 w-40">
                    <button
                      onClick={() => setShowProfile(true)}
                      className="block w-full text-left px-4 py-2 hover:bg-gray-100"
                    >
                      My Profile
                    </button>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 hover:bg-gray-100 border-t"
                    >
                      Logout
                    </button>
                  </div>
                </li>
              </>
            ) : (
              <>
                <li>
                  <Link to="/login" className="text-gray-700 hover:text-purple-700 font-medium">
                    Login
                  </Link>
                </li>
                <li>
                  <Link
                    to="/register"
                    className="bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
                  >
                    Register
                  </Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </nav>

      {/* Profile Modal */}
      {showProfile && <ProfileDialog onClose={() => setShowProfile(false)} />}
    </>
  );
}
