import React, { useEffect, useState } from "react";
import API from "../api";

export default function Jobs() {
  const [jobs, setJobs] = useState([]);
  const [q, setQ] = useState("");
  const [filteredJobs, setFilteredJobs] = useState([]);

  useEffect(() => {
    load();
  }, []);

  // Load all jobs from API
  const load = async () => {
    try {
      const res = await API.get("/jobs");
      setJobs(res.data);
      setFilteredJobs(res.data);
    } catch (err) {
      console.error("Failed to fetch jobs:", err);
    }
  };

  // Handle search (by job title or company name)
  const handleSearch = () => {
    const searchTerm = q.toLowerCase().trim();
    if (!searchTerm) {
      setFilteredJobs(jobs);
      return;
    }
    const filtered = jobs.filter((job) => {
      const title = job.title?.toLowerCase() || "";
      const company = job.company?.name?.toLowerCase() || "";
      return title.includes(searchTerm) || company.includes(searchTerm);
    });
    setFilteredJobs(filtered);
  };

  return (
    <div>
      <div className="navbar">Job Tracker</div>
      <div className="container">
        <h2>Jobs</h2>

        {/* Search bar */}
        <div className="card" style={{ display: "flex", alignItems: "center" }}>
          <input
            placeholder="Search by job or company"
            value={q}
            onChange={(e) => setQ(e.target.value)}
            style={{ flex: 1, padding: "8px" }}
          />
          <button
            className="btn"
            style={{ marginLeft: 8 }}
            onClick={handleSearch}
          >
            Search
          </button>
        </div>

        {/* Job cards */}
        <div
          style={{
            marginTop: 12,
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit,minmax(280px,1fr))",
            gap: 12,
          }}
        >
          {filteredJobs.length === 0 ? (
            <div>No jobs found</div>
          ) : (
            filteredJobs.map((j) => (
              <div className="card" key={j._id}>
                <h3 style={{ color: "#6b21a8" }}>{j.title}</h3>
                <div style={{ fontSize: 13, color: "#666" }}>
                  {j.company?.name} • {j.location} • {j.type}
                </div>
                <p>{j.description?.slice(0, 200)}...</p>

                {/* Skills fix */}
                <div style={{ marginTop: 8 }}>
                  <strong>Skills:</strong>{" "}
                  {Array.isArray(j.skills) && j.skills.length > 0
                    ? j.skills.map((s) => s.name).join(", ")
                    : "No skills listed"}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
