import React, { useEffect, useState } from "react";
import API from "../api";

export default function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false);

  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [skills, setSkills] = useState("");
  const [imagePreview, setImagePreview] = useState("");
  const [cvPreview, setCvPreview] = useState("");

  const [imageFile, setImageFile] = useState(null);
  const [cvFile, setCvFile] = useState(null);

  // Fetch profile data
  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/auth/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUser(res.data);
      setName(res.data.name);
      setPhone(res.data.phone || "");
      setSkills(res.data.skills?.join(", ") || "");
      setImagePreview(res.data.image || "");
      setCvPreview(res.data.cv || "");
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProfile();
  }, []);

  const handleEdit = async () => {
    try {
      const token = localStorage.getItem("token");

      const formData = new FormData();
      formData.append("name", name);
      formData.append("phone", phone);
      formData.append("skills", skills);
      if (imageFile) formData.append("image", imageFile);
      if (cvFile) formData.append("cv", cvFile);

      const res = await API.patch("/auth/profile", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });

      setUser(res.data);
      setEditMode(false);
      alert("Profile updated successfully!");
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || "Failed to update profile");
    }
  };

  if (loading)
    return <p style={{ textAlign: "center", marginTop: "50px" }}>Loading...</p>;

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Profile</h2>

      <div style={styles.card}>
        <div style={styles.imageSection}>
          <img
            src={imagePreview || "/default-user.png"}
            alt="Profile"
            style={styles.image}
          />
          {editMode && (
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                setImageFile(e.target.files[0]);
                setImagePreview(URL.createObjectURL(e.target.files[0]));
              }}
              style={styles.fileInput}
            />
          )}
        </div>

        <div style={styles.infoSection}>
          <div style={styles.field}>
            <label style={styles.label}>Name:</label>
            {editMode ? (
              <input
                value={name}
                onChange={(e) => setName(e.target.value)}
                style={styles.input}
              />
            ) : (
              <p>{user.name}</p>
            )}
          </div>

          <div style={styles.field}>
            <label style={styles.label}>Phone:</label>
            {editMode ? (
              <input
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                style={styles.input}
              />
            ) : (
              <p>{user.phone || "-"}</p>
            )}
          </div>

          <div style={styles.field}>
            <label style={styles.label}>Skills:</label>
            {editMode ? (
              <input
                value={skills}
                onChange={(e) => setSkills(e.target.value)}
                placeholder="Comma separated"
                style={styles.input}
              />
            ) : (
              <p>{user.skills?.join(", ") || "-"}</p>
            )}
          </div>

          <div style={styles.field}>
            <label style={styles.label}>CV:</label>
            {editMode ? (
              <>
                <input
                  type="file"
                  accept=".pdf,.doc,.docx"
                  onChange={(e) => {
                    setCvFile(e.target.files[0]);
                    setCvPreview(e.target.files[0]?.name);
                  }}
                  style={styles.fileInput}
                />
                {cvPreview && <p>Selected: {cvPreview}</p>}
              </>
            ) : user.cv ? (
              <a
                href={user.cv}
                target="_blank"
                rel="noopener noreferrer"
                style={styles.cvLink}
              >
                View CV
              </a>
            ) : (
              <p>-</p>
            )}
          </div>

          <div style={styles.buttonRow}>
            {editMode ? (
              <>
                <button onClick={() => setEditMode(false)} style={styles.cancelBtn}>
                  Cancel
                </button>
                <button onClick={handleEdit} style={styles.saveBtn}>
                  Save
                </button>
              </>
            ) : (
              <button onClick={() => setEditMode(true)} style={styles.editBtn}>
                Edit Profile
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: { maxWidth: "700px", margin: "20px auto", padding: "10px" },
  heading: { textAlign: "center", color: "#6b3fa0", marginBottom: "20px" },
  card: {
    display: "flex",
    gap: "20px",
    padding: "20px",
    background: "#fff",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    alignItems: "flex-start",
  },
  imageSection: { flex: "0 0 150px", textAlign: "center" },
  image: { width: "120px", height: "120px", borderRadius: "50%", objectFit: "cover" },
  fileInput: { marginTop: "10px" },
  infoSection: { flex: 1, display: "flex", flexDirection: "column", gap: "15px" },
  field: { display: "flex", flexDirection: "column" },
  label: { fontWeight: "bold", color: "#333", marginBottom: "5px" },
  input: {
    padding: "10px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    fontSize: "14px",
  },
  buttonRow: { display: "flex", gap: "10px", marginTop: "15px", justifyContent: "flex-end" },
  editBtn: {
    background: "#6b3fa0",
    color: "#fff",
    border: "none",
    padding: "10px 15px",
    borderRadius: "8px",
    cursor: "pointer",
  },
  saveBtn: {
    background: "#4caf50",
    color: "#fff",
    border: "none",
    padding: "10px 15px",
    borderRadius: "8px",
    cursor: "pointer",
  },
  cancelBtn: {
    background: "#ccc",
    color: "#333",
    border: "none",
    padding: "10px 15px",
    borderRadius: "8px",
    cursor: "pointer",
  },
  cvLink: { color: "#6b3fa0", textDecoration: "underline" },
};
