import React, { useEffect, useState } from "react";
import API from "../api";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [skills, setSkills] = useState([]);

  const [form, setForm] = useState({
    title: "",
    description: "",
    companyId: "",
    location: "",
    category: "",
    type: "",
    skills: [],
  });

  const [editingJob, setEditingJob] = useState(null);
  const [showDialog, setShowDialog] = useState(false);

  useEffect(() => {
    fetchJobs();
    fetchCompanies();
    fetchSkills();
  }, []);

  const fetchJobs = async () => {
    const res = await API.get("/jobs");
    setJobs(res.data);
  };

  const fetchCompanies = async () => {
    const res = await API.get("/companies");
    setCompanies(res.data);
  };

  const fetchSkills = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/skills/list", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSkills(res.data);
    } catch (err) {
      console.error("Error fetching skills:", err);
    }
  };

  const openDialog = (job = null) => {
    if (job) {
      setEditingJob(job);
      setForm({
        title: job.title,
        description: job.description,
        companyId: job.company?._id || "",
        location: job.location || "",
        category: job.category || "",
        type: job.type || "",
        skills: job.skills || [],
      });
    } else {
      setEditingJob(null);
      setForm({
        title: "",
        description: "",
        companyId: "",
        location: "",
        category: "",
        type: "",
        skills: [],
      });
    }
    setShowDialog(true);
  };

  const handleChange = (key, value) => {
    setForm((prev) => ({ ...prev, [key]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingJob) {
        await API.patch(`/jobs/${editingJob._id}`, form);
        alert("Job updated successfully!");
      } else {
        await API.post("/jobs", form);
        alert("Job added successfully!");
      }
      setShowDialog(false);
      fetchJobs();
    } catch (err) {
      alert(err.response?.data?.message || "Error saving job");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this job?")) {
      await API.delete(`/jobs/${id}`);
      alert("Job deleted successfully!");
      fetchJobs();
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Job Management</h2>

      <div style={styles.topBar}>
        <button style={styles.addBtn} onClick={() => openDialog()}>
          + Add Job
        </button>
      </div>

      <table style={styles.table}>
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={styles.th}>Title</th>
            <th style={styles.th}>Description</th>
            <th style={styles.th}>Company</th>
            <th style={styles.th}>Location</th>
            <th style={styles.th}>Category</th>
            <th style={styles.th}>Type</th>
            <th style={styles.th}>Skills</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {jobs.map((j) => (
            <tr key={j._id} style={styles.tr}>
              <td style={styles.td}>{j.title}</td>
              <td style={styles.td}>{j.description}</td>
              <td style={styles.td}>{j.company?.name}</td>
              <td style={styles.td}>{j.location}</td>
              <td style={styles.td}>{j.category}</td>
              <td style={styles.td}>{j.type}</td>
              <td style={styles.td}>{j.skills?.join(", ")}</td>
              <td style={styles.td}>
                <button onClick={() => openDialog(j)} style={styles.editBtn}>
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(j._id)}
                  style={styles.deleteBtn}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Dialog Box */}
      {showDialog && (
        <div style={styles.overlay}>
          <div style={styles.dialog}>
            <h3 style={styles.dialogTitle}>
              {editingJob ? "Edit Job" : "Add New Job"}
            </h3>

            <form onSubmit={handleSubmit} style={styles.form}>
              {/* Title + Description */}
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Title</label>
                  <input
                    style={styles.input}
                    value={form.title}
                    onChange={(e) => handleChange("title", e.target.value)}
                    required
                  />
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Description</label>
                  <input
                    style={styles.input}
                    value={form.description}
                    onChange={(e) =>
                      handleChange("description", e.target.value)
                    }
                    required
                  />
                </div>
              </div>

              {/* Company + Location */}
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Company</label>
                  <select
                    style={styles.input}
                    value={form.companyId}
                    onChange={(e) => handleChange("companyId", e.target.value)}
                    required
                  >
                    <option value="">Select Company</option>
                    {companies.map((c) => (
                      <option key={c._id} value={c._id}>
                        {c.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Location</label>
                  <input
                    style={styles.input}
                    value={form.location}
                    onChange={(e) => handleChange("location", e.target.value)}
                  />
                </div>
              </div>

              {/* Category + Type */}
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Category</label>
                  <input
                    style={styles.input}
                    value={form.category}
                    onChange={(e) => handleChange("category", e.target.value)}
                  />
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Job Type</label>
                  <select
                    style={styles.input}
                    value={form.type}
                    onChange={(e) => handleChange("type", e.target.value)}
                  >
                    <option value="">Select Type</option>
                    <option value="full-time">Full-Time</option>
                    <option value="part-time">Part-Time</option>
                    <option value="internship">Internship</option>
                  </select>
                </div>
              </div>

              {/* Skills from Skill Collection */}
              <div style={styles.field}>
                <label style={styles.label}>Required Skills</label>
                <select
                  multiple
                  style={styles.multiselect}
                  value={form.skills}
                  onChange={(e) =>
                    handleChange(
                      "skills",
                      Array.from(e.target.selectedOptions, (opt) => opt.value)
                    )
                  }
                >
                  {skills.map((skill) => (
                    <option key={skill._id} value={skill.name}>
                      {skill.name}
                    </option>
                  ))}
                </select>
              </div>

              <div style={styles.btnRow}>
                <button type="submit" style={styles.saveBtn}>
                  {editingJob ? "Update" : "Save"}
                </button>
                <button
                  type="button"
                  style={styles.cancelBtn}
                  onClick={() => setShowDialog(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

