import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user") || "{}");
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <nav className="bg-gray-900 text-white shadow-md">
      <div className="container mx-auto px-4 py-3 flex items-center justify-between">
        {/* Logo Section */}
        <Link to="/" className="flex items-center gap-2 text-xl font-semibold">
          <img src="/logo.png" alt="Logo" className="w-8 h-8" />
          <span>JobFinder</span>
        </Link>

        {/* Mobile Menu Toggle */}
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          className="md:hidden focus:outline-none"
        >
          <svg
            className="w-6 h-6"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            viewBox="0 0 24 24"
          >
            {menuOpen ? (
              <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
            ) : (
              <path strokeLinecap="round" strokeLinejoin="round" d="M4 6h16M4 12h16M4 18h16" />
            )}
          </svg>
        </button>

        {/* Links */}
        <div
          className={`${
            menuOpen ? "block" : "hidden"
          } w-full md:flex md:items-center md:w-auto`}
        >
          <ul className="md:flex md:space-x-6 mt-3 md:mt-0 text-center md:text-left">
            <li>
              <Link to="/jobs" className="block py-2 hover:text-blue-400">
                Jobs
              </Link>
            </li>

            {token ? (
              <>
                <li>
                  <Link to="/dashboard" className="block py-2 hover:text-blue-400">
                    Dashboard
                  </Link>
                </li>
                <li className="relative group">
                  <button className="block py-2 hover:text-blue-400">
                    {user?.name || "Profile"}
                  </button>
                  <div className="absolute right-0 mt-1 hidden group-hover:block bg-gray-800 rounded-lg shadow-lg w-40 text-left">
                    <Link
                      to="/profile"
                      className="block px-4 py-2 hover:bg-gray-700 rounded-t-lg"
                    >
                      My Profile
                    </Link>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 hover:bg-gray-700 rounded-b-lg"
                    >
                      Logout
                    </button>
                  </div>
                </li>
              </>
            ) : (
              <>
                <li>
                  <Link to="/login" className="block py-2 hover:text-blue-400">
                    Login
                  </Link>
                </li>
                <li>
                  <Link
                    to="/register"
                    className="block py-2 px-4 bg-blue-600 hover:bg-blue-700 rounded-lg"
                  >
                    Register
                  </Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}
