import React, { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import API from "../api";

export default function CompanyList() {
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [modalOpen, setModalOpen] = useState(false);
  const [editId, setEditId] = useState(null);
  const [logoPreview, setLogoPreview] = useState(null);

  const API_URL = process.env.REACT_APP_API_URL || "http://localhost:7000";

  const {
    register,
    handleSubmit,
    setValue,
    reset,
    formState: { errors },
  } = useForm();

  // Fetch companies
  const fetchCompanies = async () => {
    try {
      const res = await API.get("/companies");
      setCompanies(res.data);
    } catch (err) {
      console.error(err);
      toast.error("Failed to fetch companies");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCompanies();
  }, []);

  // Open modal for Add/Edit
  const handleOpenModal = (company = null) => {
    if (company) {
      setEditId(company._id);
      setValue("name", company.name);
      setValue("description", company.description);
      setValue("website", company.website);
      setLogoPreview(company.logo ? `${API_URL}${company.logo}` : null);
    } else {
      setEditId(null);
      reset();
      setLogoPreview(null);
    }
    setModalOpen(true);
  };

  const handleCloseModal = () => {
    reset();
    setLogoPreview(null);
    setModalOpen(false);
  };

  const handleLogoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setValue("logo", file);
      setLogoPreview(URL.createObjectURL(file));
    }
  };

  const onSubmit = async (data) => {
    const formData = new FormData();
    formData.append("name", data.name);
    formData.append("description", data.description || "");
    formData.append("website", data.website || "");
    if (data.logo) formData.append("logo", data.logo);

    const token = localStorage.getItem("token");

    try {
      if (editId) {
        await API.patch(`/companies/${editId}`, formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        });
        toast.success("Company updated successfully!");
      } else {
        await API.post("/companies", formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        });
        toast.success("Company added successfully!");
      }

      fetchCompanies();
      handleCloseModal();
    } catch (err) {
      console.error(err);
      toast.error(err.response?.data?.message || "Failed to save company");
    }
  };

  // Delete
  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this company?")) return;
    try {
      const token = localStorage.getItem("token");
      await API.delete(`/companies/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      toast.success("Company deleted successfully!");
      fetchCompanies();
    } catch (err) {
      console.error(err);
      toast.error(err.response?.data?.message || "Failed to delete company");
    }
  };

  if (loading) return <p style={{ textAlign: "center" }}>Loading...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <ToastContainer position="top-right" autoClose={2000} />

      <h2
        style={{
          color: "#6b3fa0",
          textAlign: "center",
          marginBottom: "20px",
        }}
      >
        Company Management
      </h2>

      <div style={{ textAlign: "right", marginBottom: "10px" }}>
        <button
          onClick={() => handleOpenModal()}
          style={{
            padding: "8px 16px",
            backgroundColor: "#6b3fa0",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          + Add Company
        </button>
      </div>

      {/* Modal */}
      {modalOpen && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100%",
            height: "100%",
            background: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            style={{
              background: "#fff",
              padding: "20px",
              borderRadius: "10px",
              width: "400px",
              maxWidth: "90%",
              position: "relative",
            }}
          >
            <h3 style={{ marginBottom: "15px", color: "#6b3fa0" }}>
              {editId ? "Edit Company" : "Add Company"}
            </h3>

            <form
              onSubmit={handleSubmit(onSubmit)}
              style={{ display: "flex", flexDirection: "column", gap: "10px" }}
            >
              {/* Name */}
              <input
                type="text"
                placeholder="Company Name"
                {...register("name", { required: "Name is required" })}
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ccc",
                }}
              />
              {errors.name && (
                <p style={{ color: "red", margin: 0 }}>
                  {errors.name.message}
                </p>
              )}

              {/* Description */}
              <input
                type="text"
                placeholder="Description"
                {...register("description")}
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ccc",
                }}
              />

              {/* Website */}
              <input
                type="text"
                placeholder="Website"
                {...register("website")}
                style={{
                  padding: "8px",
                  borderRadius: "5px",
                  border: "1px solid #ccc",
                }}
              />

              {/* Logo */}
              <input
                type="file"
                accept="image/*"
                {...register("logo")}
                onChange={handleLogoChange}
              />

              {logoPreview && (
                <img
                  src={logoPreview}
                  alt="preview"
                  width={80}
                  height={80}
                  style={{ borderRadius: "8px" }}
                />
              )}

              <div
                style={{
                  display: "flex",
                  justifyContent: "flex-end",
                  gap: "10px",
                  marginTop: "10px",
                }}
              >
                <button
                  type="button"
                  onClick={handleCloseModal}
                  style={{
                    padding: "8px 12px",
                    borderRadius: "5px",
                    border: "1px solid #ccc",
                    background: "#f0f0f0",
                    cursor: "pointer",
                  }}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  style={{
                    padding: "8px 12px",
                    borderRadius: "5px",
                    border: "none",
                    background: "#6b3fa0",
                    color: "#fff",
                    cursor: "pointer",
                  }}
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Table */}
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
          boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
          borderRadius: "10px",
          overflow: "hidden",
          backgroundColor: "#fff",
        }}
      >
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={{ padding: "12px" }}>Logo</th>
            <th style={{ padding: "12px" }}>Name</th>
            <th style={{ padding: "12px" }}>Description</th>
            <th style={{ padding: "12px" }}>Website</th>
            <th style={{ padding: "12px", textAlign: "center" }}>Action</th>
          </tr>
        </thead>
        <tbody>
          {companies.length > 0 ? (
            companies.map((c) => (
              <tr key={c._id} style={{ borderBottom: "1px solid #ddd" }}>
                <td style={{ padding: "10px", textAlign: "center" }}>
                  <img
                    src={
                      c.logo
                        ? `${API_URL}${c.logo}`
                        : `${API_URL}/uploads/default.png`
                    }
                    alt={c.name}
                    width={60}
                    height={60}
                    style={{ borderRadius: "8px", objectFit: "cover" }}
                  />
                </td>
                <td style={{ padding: "10px", fontWeight: "500" }}>{c.name}</td>
                <td style={{ padding: "10px" }}>{c.description}</td>
                <td style={{ padding: "10px" }}>
                  <a href={c.website} target="_blank" rel="noreferrer">
                    {c.website}
                  </a>
                </td>
                <td style={{ padding: "10px", textAlign: "center" }}>
                  <button
                    onClick={() => handleOpenModal(c)}
                    style={{
                      padding: "6px 12px",
                      backgroundColor: "#6b3fa0",
                      color: "#fff",
                      border: "none",
                      borderRadius: "5px",
                      cursor: "pointer",
                      marginRight: "5px",
                    }}
                  >
                    ✏️ Edit
                  </button>
                  <button
                    onClick={() => handleDelete(c._id)}
                    style={{
                      padding: "6px 12px",
                      backgroundColor: "#ff4d4f",
                      color: "#fff",
                      border: "none",
                      borderRadius: "5px",
                      cursor: "pointer",
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" style={{ padding: "20px", textAlign: "center" }}>
                No companies found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
