import React, { useEffect, useState, useRef } from "react";
import API from "../api";
import { useForm } from "react-hook-form";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function JobList() {
  const [jobs, setJobs] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [skills, setSkills] = useState([]);

  const [editingJob, setEditingJob] = useState(null);
  const [showDialog, setShowDialog] = useState(false);
  const [showSkillDropdown, setShowSkillDropdown] = useState(false);

  const dropdownRef = useRef(null);

  const { register, handleSubmit, setValue, reset, watch } = useForm({
    defaultValues: {
      title: "",
      description: "",
      companyId: "",
      location: "",
      category: "",
      type: "",
      skills: [],
    },
  });

  const watchedSkills = watch("skills");

  useEffect(() => {
    fetchJobs();
    fetchCompanies();
    fetchSkills();
  }, []);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowSkillDropdown(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () =>
      document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const fetchJobs = async () => {
    const res = await API.get("/jobs");
    setJobs(res.data);
  };

  const fetchCompanies = async () => {
    const res = await API.get("/companies");
    setCompanies(res.data);
  };

  const fetchSkills = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await API.get("/skills/list", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSkills(res.data);
    } catch (err) {
      console.error("Error fetching skills:", err);
    }
  };

  const openDialog = (job = null) => {
    if (job) {
      setEditingJob(job);
      reset({
        title: job.title,
        description: job.description,
        companyId: job.company?._id || "",
        location: job.location || "",
        category: job.category || "",
        type: job.type || "",
        skills: job.skills?.map((s) => (s._id ? s._id : s)) || [],
      });
    } else {
      setEditingJob(null);
      reset({
        title: "",
        description: "",
        companyId: "",
        location: "",
        category: "",
        type: "",
        skills: [],
      });
    }
    setShowDialog(true);
  };

  const toggleSkill = (id) => {
    const currentSkills = watchedSkills || [];
    if (currentSkills.includes(id)) {
      setValue(
        "skills",
        currentSkills.filter((s) => s !== id)
      );
    } else {
      setValue("skills", [...currentSkills, id]);
    }
  };

  const onSubmit = async (data) => {
    try {
      if (editingJob) {
        await API.patch(`/jobs/${editingJob._id}`, data);
        toast.success("Job updated successfully!");
      } else {
        await API.post("/jobs", data);
        toast.success("Job added successfully!");
      }
      setShowDialog(false);
      fetchJobs();
    } catch (err) {
      toast.error(err.response?.data?.message || "Error saving job");
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this job?")) {
      await API.delete(`/jobs/${id}`);
      toast.success("Job deleted successfully!");
      fetchJobs();
    }
  };

  return (
    <div style={styles.container}>
      <ToastContainer position="top-right" />
      <h2 style={styles.heading}>Job Management</h2>

      <div style={styles.topBar}>
        <button style={styles.addBtn} onClick={() => openDialog()}>
          + Add Job
        </button>
      </div>

      <table style={styles.table}>
        <thead style={{ background: "#ede4ff" }}>
          <tr>
            <th style={styles.th}>Title</th>
            <th style={styles.th}>Description</th>
            <th style={styles.th}>Company</th>
            <th style={styles.th}>Logo</th>
            <th style={styles.th}>Location</th>
            <th style={styles.th}>Category</th>
            <th style={styles.th}>Type</th>
            <th style={styles.th}>Skills</th>
            <th style={styles.th}>Actions</th>
          </tr>
        </thead>
        <tbody>
          {jobs.map((j) => (
            <tr key={j._id} style={styles.tr}>
              <td style={styles.td}>{j.title}</td>
              <td style={styles.td}>{j.description}</td>
              <td style={styles.td}>{j.company?.name}</td>
              <td style={styles.td}>
                {j.company?.image ? (
                  <img
                    src={`http://localhost:5000/${j.company.image}`}
                    alt="Logo"
                    style={{ width: "50px", height: "50px", objectFit: "cover" }}
                  />
                ) : (
                  "No Image"
                )}
              </td>
              <td style={styles.td}>{j.location}</td>
              <td style={styles.td}>{j.category}</td>
              <td style={styles.td}>{j.type}</td>
              <td style={styles.td}>
                {Array.isArray(j.skills)
                  ? j.skills
                      .map((s) => (typeof s === "object" ? s.name : s))
                      .join(", ")
                  : ""}
              </td>
              <td style={styles.td}>
                <button onClick={() => openDialog(j)} style={styles.editBtn}>
                  ✏️ Edit
                </button>
                <button
                  onClick={() => handleDelete(j._id)}
                  style={styles.deleteBtn}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showDialog && (
        <div style={styles.overlay}>
          <div style={styles.dialog}>
            <h3 style={styles.dialogTitle}>
              {editingJob ? "Edit Job" : "Add New Job"}
            </h3>

            <form onSubmit={handleSubmit(onSubmit)} style={styles.form}>
              {/* Title + Description */}
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Title</label>
                  <input
                    style={styles.input}
                    {...register("title", { required: true })}
                  />
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Description</label>
                  <input
                    style={styles.input}
                    {...register("description", { required: true })}
                  />
                </div>
              </div>

              {/* Company + Location */}
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Company</label>
                  <select
                    style={styles.input}
                    {...register("companyId", { required: true })}
                  >
                    <option value="">Select Company</option>
                    {companies.map((c) => (
                      <option key={c._id} value={c._id}>
                        {c.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Location</label>
                  <input style={styles.input} {...register("location")} />
                </div>
              </div>

              {/* Category + Type */}
              <div style={styles.row}>
                <div style={styles.field}>
                  <label style={styles.label}>Category</label>
                  <input style={styles.input} {...register("category")} />
                </div>
                <div style={styles.field}>
                  <label style={styles.label}>Job Type</label>
                  <select style={styles.input} {...register("type")}>
                    <option value="">Select Type</option>
                    <option value="full-time">Full-Time</option>
                    <option value="part-time">Part-Time</option>
                    <option value="internship">Internship</option>
                  </select>
                </div>
              </div>

              {/* Skills Dropdown */}
              <div style={{ ...styles.field, position: "relative" }} ref={dropdownRef}>
                <label style={styles.label}>Required Skills</label>
                <div
                  style={styles.dropdownBox}
                  onClick={() => setShowSkillDropdown(!showSkillDropdown)}
                >
                  <div
                    style={{
                      display: "inline-block",
                      maxWidth: "85%",
                      overflow: "hidden",
                      textOverflow: "ellipsis",
                      whiteSpace: "nowrap",
                      verticalAlign: "middle",
                    }}
                  >
                    {watchedSkills.length > 0
                      ? skills
                          .filter((s) => watchedSkills.includes(s._id))
                          .map((s) => s.name)
                          .join(", ")
                      : "Select Skills"}
                  </div>
                  <span style={{ float: "right" }}>▼</span>
                </div>

                {showSkillDropdown && (
                  <div style={styles.dropdownList}>
                    {skills.map((skill) => (
                      <div
                        key={skill._id}
                        style={{
                          ...styles.dropdownItem,
                          background: watchedSkills.includes(skill._id)
                            ? "#e6d7ff"
                            : "white",
                        }}
                        onClick={() => toggleSkill(skill._id)}
                      >
                        <input
                          type="checkbox"
                          checked={watchedSkills.includes(skill._id)}
                          readOnly
                        />
                        <span style={{ marginLeft: "8px" }}>{skill.name}</span>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              <div style={styles.btnRow}>
                <button type="submit" style={styles.saveBtn}>
                  {editingJob ? "Update" : "Save"}
                </button>
                <button
                  type="button"
                  style={styles.cancelBtn}
                  onClick={() => setShowDialog(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

// Styles remain mostly the same
