import React, { useState } from 'react';
import API from '../api';

export default function Login(){
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const handle = async (e) => {
    e.preventDefault();
    try{
      const res = await API.post('http://localhost:7000/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      window.location.href = res.data.user.role === 'admin' ? '/admin' : '/jobs';
    }catch(err){
      alert(err.response?.data?.message || 'Login failed');
    }
  }
  return (<div>
    <div className="navbar">Job Tracker</div>
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handle} className="card">
        <div><label>Email</label><br/><input value={email} onChange={e=>setEmail(e.target.value)} /></div>
        <div style={{marginTop:8}}><label>Password</label><br/><input type="password" value={password} onChange={e=>setPassword(e.target.value)} /></div>
        <div style={{marginTop:8}}><button className="btn">Login</button></div>
      </form>
    </div>
  </div>)
}
